import cardStyle from "./Card.module.css";
import { NavLink } from "react-router-dom";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Product from "../Product/Product";
export default function Card(props) {
  return (
    <Router>
      <Routes>
        <NavLink to="/product" className={cardStyle.card}>
          <img className={cardStyle.img} src={props.image}></img>
          <p className={cardStyle.name}>{props.name}</p>
          <p className={cardStyle.price}>{props.price} $</p>
        </NavLink>
        <Route
          path="/product"
          render={(props) => <Product {...props} state={props} />}
        />
      </Routes>
    </Router>
  );
}
