import productStyle from "./Product.module.css";
import data from "../../utils/data";
import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
export default function Product(props) {
  const [src, setSrc] = useState("../../logo/Group 4.png");
  const largeImg = (imgSrc) => {
    console.log(imgSrc);
    setSrc(imgSrc);
  };
  useEffect(() => {
    setSrc("https://www.bsau.ru/upload/iblock/0ef/10.jpg");
  }, []);
  const { state } = useLocation();
  const { id, color } = state;
  console.log(props);
  return (
    <div className={productStyle.mainBox}>
      <div className={productStyle.box}>
        <div className={productStyle.imgBox}>
          {data.map((obj) => {
            if (obj.id <= 4) {
              return (
                <img
                  key={obj.id}
                  onClick={() => largeImg(obj.image)}
                  className={productStyle.imgMin}
                  src={obj.image}
                ></img>
              );
            }
          })}
        </div>
        <img className={productStyle.imgLarge} src={src}></img>
      </div>
      <div className={productStyle.infoBox}>
        <div className={productStyle.headerBox}>
          <h2 className={productStyle.name}>erterterter</h2>
          <p className={productStyle.price}>4343</p>
        </div>
        <hr></hr>
        <p className={productStyle.about}>
          kfbhwefbweifvbwuefvweuvff weighfiwegfiwegfiwegf wigfwioegfiwegfiowe
          gweifugweiufgw
        </p>
        <div className={productStyle.sizesBox}></div>
        <button className={productStyle.btn}></button>
      </div>
    </div>
  );
}
