import { GET_CARDS } from "../actions/cards";
import { ICard } from "../constants";

interface IGetCards {
  readonly type: typeof GET_CARDS;
  readonly data: ICard[];
  readonly payload: IQuestionmainData;
}

type TinitialState = {
  cards: ICard[];
};

const initialState: TinitialState = {
  cards: [],
};

export const cardsReducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_CARDS: {
      return {
        ...state,
        cards: action.payload.data,
      };
    }

    default: {
      return state;
    }
  }
};
