import cardElementStyles from "./CardElement.module.css";
import data from "../../utils/data";
import React, { useEffect, useMemo } from "react";
import { useDispatch, useSelector } from "react-redux";
import { REMOVE_ITEM, RESET_ITEM } from "../../services/actions/cardItems";
export default function CardElement({ onClose }) {
  const Carditems = useSelector((store) => store.cardItem.items);
  const dispatch = useDispatch();
  function onlyUnique(value, index, array) {
    return array.indexOf(value) === index;
  }
  const removeItem = (id4) => {
    dispatch({ type: REMOVE_ITEM, id4 });
  };
  const resetItem = () => {
    dispatch({ type: RESET_ITEM });
  };
  useEffect(() => {
    if (Carditems.length === 0) {
      onClose();
    }
  }, [removeItem]);
  const fullPrice = useMemo(() => {
    let price = 0;
    Carditems.forEach((item) => (price += item.props.price));
    return price ? price : 0;
  }, [Carditems]);
  return (
    <div className={cardElementStyles.main}>
      <button
        onClick={() => resetItem()}
        className={cardElementStyles.resetBtn}
      >
        {" "}
        Reset Card
      </button>
      {Carditems.map((items) => {
        let item = items.props;
        let k = 0;
        item.data = 0;

        let r = Carditems;
        let v = data;
        r.forEach((obj) => {
          let l = [];
          console.log("1.1");
          console.log(l);
          console.log("2.1");
          console.log(r);
          if (item.image === obj.props.image) {
            item.data++;
          }
          v.reverse(
            forEach((sas) => {
              if (sas.image === obj.props.image) {
                l.push(sas);
                console.log("1.2");
                console.log(l);
                v.filter((u) => u.id !== obj.props.id);
                console.log("2.2");
                console.log(v);
              }
            })
          );
          /* if (data.image === obj.props.image) {
            l.push(item);
            console.log("1.2");
            console.log(l);
            r.filter((u) => u.image === item.image);
            console.log("2.2");
            console.log(r);
          } */
        });
        if (item.data <= 1) {
          return (
            <div className={cardElementStyles.box} key={items.id4}>
              <img className={cardElementStyles.img} src={item.image}></img>
              <div className={cardElementStyles.info}>
                <p className={cardElementStyles.name}>{item.name}</p>
                <p className={cardElementStyles.price}>{item.price} $</p>
              </div>
              <button
                onClick={() => removeItem(Carditems.id4)}
                className={cardElementStyles.btn}
              >
                <img
                  onClick={() => removeItem(items.id4)}
                  className={cardElementStyles.bascet}
                  src="https://avatars.mds.yandex.net/i?id=13c7dd9528c82e0e90dad5bbd588771eb5d05453-9043236-images-thumbs&n=13"
                ></img>
              </button>
              <p className={cardElementStyles.counter}>{item.data}</p>
            </div>
          );
        }
        /*   else{
          Carditems.filter((object) => 
        } */

        /*  let m = Carditems.filter((object) => object.props.image === item.image);
        console.log(m); */
      })}
      <button className={cardElementStyles.priceBtn}>{fullPrice} $</button>
    </div>
  );
}
