import {
  MOVE_ITEM,
  REMOVE_ITEM,
  RESET_ITEM,
  ADD_ITEM,
} from "../actions/constructor.js";

const initialState = {
  items: [],
};
export const cardItemReducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_ITEM: {
      return {};
    }
  }
};
