import { GET_CARD } from "../actions/card";

const initialState = {
  cards: [],
};

export const cardReducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_CARD: {
      return {
        ...state,
        cards: action.payload.data,
      };
    }

    default: {
      return state;
    }
  }
};
