import { GET_CARDS } from "../actions/cards";

const initialState = {
  card: [],
};

export const cardsReducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_CARDS: {
      return {
        ...state,
        card: action.payload.data,
      };
    }

    default: {
      return state;
    }
  }
};
